# Define cost function
def cost(i,j):
    sub_mat = np.matrix([[0,5,2,5,5],
                     [5,0,5,2,5],
                     [2,5,0,5,5],
                     [5,2,5,0,5],
                     [5,5,5,5,0]])
    letters = {'A':0,'C':1,'G':2,'T':3,'-':4}
    return sub_mat[letters[i],letters[j]]
    
