# Step 1 approx - find the sequence as the center sequence

def fill_matrix_pairwise(mat,i,j,s1,s2,gapcost = 5):
    if mat[i][j] == np.inf:
        v1, v2, v3, v4 = np.inf,np.inf,np.inf,np.inf
        if i > 0 and j > 0:
            v1 = fill_matrix_pairwise(mat,i-1,j-1,s1,s2) + cost(s1[i-1],s2[j-1])
        if i >= 0 and j > 0:
            v2 = fill_matrix_pairwise(mat,i,j-1,s1,s2) + gapcost
        if i > 0 and j >= 0:
            v3 = fill_matrix_pairwise(mat,i-1,j,s1,s2) + gapcost
        if i == 0 and j == 0:
            v4 = 0
        mat[i][j] = min(v1,v2,v3,v4)
    return mat[i][j]

def find_center():
    s1_matrix = np.full((len(sequences),len(sequences)),0)
    for i in sequences:
        for k in sequences:
            empty_matrix_pairwise = np.full((len(i)+1,len(k)+1),np.inf)
            s1_matrix[sequences.index(k)][sequences.index(i)] = fill_matrix_pairwise(empty_matrix_pairwise,len(i),len(k),i,k)
    return np.argmin(sum(s1_matrix))
