# Define matrix for backtrack
def alignment_pairwise(s1,s2, gapcost = 5):
    matrix_for_backtrack_pairwise = view_matrix_pairwise(s1,s2)
    align_seq_1 = []
    align_seq_2 = []
    i = np.shape(matrix_for_backtrack_pairwise)[0]-1
    j = np.shape(matrix_for_backtrack_pairwise)[1]-1
    while i > 0 or j > 0:
        if matrix_for_backtrack_pairwise[i][j] == matrix_for_backtrack_pairwise[i-1][j-1] +  cost(s1[i-1],s2[j-1]):
            align_seq_1.append(s1[i-1])
            align_seq_2.append(s2[j-1])
            i -= 1
            j -= 1
        if matrix_for_backtrack_pairwise[i][j] == matrix_for_backtrack_pairwise[i-1][j] + gapcost:
            align_seq_1.append(s1[i-1])
            align_seq_2.append('-')
            i-=1
        if matrix_for_backtrack_pairwise[i][j] == matrix_for_backtrack_pairwise[i][j-1] + gapcost:
            align_seq_1.append('-')
            align_seq_2.append(s2[j-1])
            j-=1
    return np.array([align_seq_1[::-1], align_seq_2[::-1]],dtype=str)

