# Just to view the optimal score matrix
def view_matrix(s1, s2, s3):
    optimal_matrix = np.full((len(s1)+1, len(s2)+1, len(s3)+1), np.inf)
    i, j, k = len(s1), len(s2), len(s3)
    fill_matrix(optimal_matrix, i, j, k, s1, s2, s3)
    return optimal_matrix

